function isInViewport(e){let t=e.getBoundingClientRect();return t.top<=(window.innerHeight||document.documentElement.clientHeight)&&t.bottom>=0}function handleScroll(){let e=document.querySelectorAll(".import");e.forEach(e=>{isInViewport(e)?e.classList.add("show"):e.classList.remove("show")})}let isScrolling;window.addEventListener("scroll",function(){window.clearTimeout(isScrolling),isScrolling=setTimeout(function(){handleScroll()},100)},!1);